import React from 'react'
import ReactDOM from 'react-dom';


function categorize(data){
  var cats = {}
  var cat
  for (let idx in data){
      let obj = data[idx];
      cat = obj.category;
      let {price, stocked, name} = obj;
      let partialObj = {price, stocked, name}
      if (cat in cats){
          cats[cat].push(partialObj);
      }
      else{
          cats[cat] = [partialObj];
      }
  }
  return cats;
}

class ProductRow extends React.Component{
  constructor(props){
    super(props);
    this.name = props.product.name;
    this.price = props.product.price;
    this.stocked = props.product.stocked;
  }

  render(){
    return(
    <div>
      <td>{this.name}</td>
      <td>{this.price}</td>
    </div>
    )
  }
}

class ProductTable extends React.Component{
  constructor(props){
    super(props);
  }

  render(){
    var rows = (items => {
      let rows = []
      for (let cat in items){
        // rows.push(<ProductCategoryRow name={cat} />)
        let products = items[cat].map(product => <ProductRow product={product} />);
        rows.push(products);
      }
      console.log(rows);
      return rows;
    })(this.props.items);

    return(
      <table>
        <tr>
          <th>Nome</th>
          <th>Preço</th>
        </tr>
        <tr>
          {rows}
        </tr>
        {}
      </table>
    )

  }
}



class SearchBar extends React.Component{

  render(){
    return(
      <div>
        <input type='text'>Search...</input>
        <label>
        <input type="checkbox"/>
        Only show products in stock
        </label>
      </div>
    )
  }
}

class FilterableProductTable extends React.Component{
  constructor(props){
    super(props);

  this.data = [
    {category: "Sporting Goods", price: "$49.99", stocked: true, name: "Football"},
    {category: "Sporting Goods", price: "$9.99", stocked: true, name: "Baseball"},
    {category: "Sporting Goods", price: "$29.99", stocked: false, name: "Basketball"},
    {category: "Electronics", price: "$99.99", stocked: true, name: "iPod Touch"},
    {category: "Electronics", price: "$399.99", stocked: false, name: "iPhone 5"},
    {category: "Electronics", price: "$199.99", stocked: true, name: "Nexus 7"}
    ];
  }

  render(){
    var items = categorize(this.data);
    return(
    <div>
      {this.props.children}
    </div>
    )
  }
}

ReactDOM.render(<FilterableProductTable />, document.getElementById('root'));
