import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function categorize(data){
  var cats = {}
  var cat
  for (let idx in data){
      let obj = data[idx];
      cat = obj.category;
      let {price, stocked, name} = obj;
      let partialObj = {price, stocked, name}
      if (cat in cats){
          cats[cat].push(partialObj);
      }
      else{
          cats[cat] = [partialObj];
      }
  }
  return cats;
}

function ProductCategoryRow(props){
  return (
    <tr>
      <th colSpan="2" >{props.category}</th>
    </tr>
  )
}

class ProductRow extends React.Component{
  constructor(props){
    super(props);
    this.name = props.product.name;
    this.price = props.product.price;
    this.stocked = props.product.stocked;
  }

  render(){
    return(
    <tr>
      <td>{this.name}</td> 
      <td>{this.price}</td>
    </tr>
    )
  }
}

class ProductTable extends React.Component{

  render(){
    let items = categorize(this.props.items)
    var rows = (items => {
      let rows = []
      for (let cat in items){
        rows.push(<ProductCategoryRow category={cat} key={cat} />)
        let products = items[cat].map(product => <ProductRow product={product} key={product.name} />);
        rows.push(products);
      }
      console.log(rows);
      return rows;
    })(items);

    return(
      <table>
      <thead>
        <tr>
          <th>Nome</th>
          <th>Preço</th>
        </tr>
      </thead>
      <tbody>
        {rows}
      </tbody>
      </table>
    )

  }
}



class SearchBar extends React.Component{
  constructor(props){
    super(props);

  }

  render(){
    return(
      <form className='search'>
        <input type='text' placeholder="Search..." />
        <div>
        <input type="checkbox"/>
        {' '}
        Only show products in stock
        </div>
      </form>
    )
  }
}

const data = [
  {category: "Sporting Goods", price: "$49.99", stocked: true, name: "Football"},
  {category: "Sporting Goods", price: "$9.99", stocked: true, name: "Baseball"},
  {category: "Electronics", price: "$99.99", stocked: true, name: "iPod Touch"},
  {category: "Sporting Goods", price: "$29.99", stocked: false, name: "Basketball"},
  {category: "Electronics", price: "$399.99", stocked: false, name: "iPhone 5"},
  {category: "Electronics", price: "$199.99", stocked: true, name: "Nexus 7"}
  ];



function FilterableProductTable(props){
    return(
    <div className='table' >
      {props.searchBar}
      {props.productTable}
    </div>
    )
}

function App(){
    const search = <SearchBar />
    const table = <ProductTable items={data} />
    return(
      <FilterableProductTable searchBar={search} productTable={table} />

    )
  }


ReactDOM.render(<App />, document.getElementById('root'));
